import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './styles.css';

const RATE = 5.95;

function App() {
  const [clients, setClients] = useState([]);
  const [readings, setReadings] = useState({});
  const [billNetReading, setBillNetReading] = useState(0);
  const [taxFuel, setTaxFuel] = useState(0);
  const [minCharges, setMinCharges] = useState({});

  useEffect(() => {
    axios.get('http://localhost:4000/api/clients').then(res => {
      setClients(res.data);
      // Initialize readings and minCharges
      const initialReadings = {};
      const initialMinCharges = {};
      res.data.forEach(client => {
        initialReadings[client.id] = { prev: 0, present: 0 };
        initialMinCharges[client.id] = client.minCharge || 0;
      });
      setReadings(initialReadings);
      setMinCharges(initialMinCharges);
    });
  }, []);

  // Calculation logic
  const netReadings = clients.map(client => {
    const { prev, present } = readings[client.id] || {};
    let net = present - prev;
    if (client.mwh) net *= 1000;
    return net > 0 ? net : 0;
  });

  const totalNetReading = netReadings.reduce((a, b) => a + b, 0);
  const lossPercent = totalNetReading ? ((totalNetReading - billNetReading) / totalNetReading) * 100 : 0;

  const clientLoss = netReadings.map(net => net * (lossPercent / 100));
  const totalReadingRs = netReadings.map((net, i) => (net + clientLoss[i]) * RATE);
  const totalReadingSum = totalReadingRs.reduce((a, b) => a + b, 0);

  const minChargesSum = Object.values(minCharges).reduce((a, b) => a + b, 0);

  // Distribute tax/fuel equally
  const taxFuelPerClient = clients.length ? taxFuel / clients.length : 0;
  const totalAmount = clients.map((_, i) => totalReadingRs[i] + (minCharges[clients[i].id] || 0) + taxFuelPerClient);
  const totalAmountSum = totalAmount.reduce((a, b) => a + b, 0);

  // Handlers
  const handleReadingChange = (id, field, value) => {
    setReadings(r => ({ ...r, [id]: { ...r[id], [field]: Number(value) } }));
  };
  const handleMinChargeChange = (id, value) => {
    setMinCharges(m => ({ ...m, [id]: Number(value) }));
  };

  return (
    <div style={{ padding: 24, fontFamily: 'sans-serif' }}>
      <h2>SV Plaza Electricity Bill Calculator</h2>
      <table border="1" cellPadding="8" style={{ width: '100%', marginBottom: 16 }}>
        <thead>
          <tr>
            <th>Sl. No.</th>
            <th>Name</th>
            <th>Previous Reading</th>
            <th>Present Reading</th>
            <th>Net Reading</th>
            <th>Min Charges (Rs)</th>
            <th>Total Reading (Rs)</th>
            <th>Total Amount (Rs)</th>
          </tr>
        </thead>
        <tbody>
          {clients.map((client, i) => (
            <tr key={client.id}>
              <td>{i + 1}</td>
              <td>{client.name}</td>
              <td><input type="number" value={readings[client.id]?.prev || ''} onChange={e => handleReadingChange(client.id, 'prev', e.target.value)} /></td>
              <td><input type="number" value={readings[client.id]?.present || ''} onChange={e => handleReadingChange(client.id, 'present', e.target.value)} /></td>
              <td>{netReadings[i]}</td>
              <td><input type="number" value={minCharges[client.id] || ''} onChange={e => handleMinChargeChange(client.id, e.target.value)} /></td>
              <td>{totalReadingRs[i].toFixed(2)}</td>
              <td>{totalAmount[i].toFixed(2)}</td>
            </tr>
          ))}
        </tbody>
        <tfoot>
          <tr>
            <td colSpan={4}><b>Total Net Reading</b></td>
            <td><b>{totalNetReading}</b></td>
            <td><b>{minChargesSum}</b></td>
            <td><b>{totalReadingSum.toFixed(2)}</b></td>
            <td><b>{totalAmountSum.toFixed(2)}</b></td>
          </tr>
        </tfoot>
      </table>
      <div style={{ marginBottom: 16 }}>
        <label>Bill Net Reading (main meter): <input type="number" value={billNetReading} onChange={e => setBillNetReading(Number(e.target.value))} /></label>
        <span style={{ marginLeft: 24 }}>Loss %: <b>{lossPercent.toFixed(2)}</b></span>
      </div>
      <div style={{ marginBottom: 16 }}>
        <label>Tax + Fuel Adjustment Charges (Rs): <input type="number" value={taxFuel} onChange={e => setTaxFuel(Number(e.target.value))} /></label>
      </div>
      <div>
        <b>Summary:</b><br />
        Total Net Reading: {totalNetReading}<br />
        Total Reading (Rs): {totalReadingSum.toFixed(2)}<br />
        Total Minimum Charges: {minChargesSum}<br />
        Total Tax/Fuel Charges: {taxFuel}<br />
        <b>Total Amount (Rs): {totalAmountSum.toFixed(2)}</b>
      </div>
    </div>
  );
}

export default App;
